export function parseTakeInstruction<
  TProgram extends string,
  TAccountMetas extends readonly AccountMeta[],
>(
  instruction: Instruction<TProgram> &
    InstructionWithAccounts<TAccountMetas> &
    InstructionWithData<ReadonlyUint8Array>
): ParsedTakeInstruction<TProgram, TAccountMetas> {
  if (instruction.accounts.length < 13) {
    // TODO: Coded error.
    throw new Error('Not enough accounts');
  }
  let accountIndex = 0;
  const getNextAccount = () => {
    const accountMeta = (instruction.accounts as TAccountMetas)[accountIndex]!;
    accountIndex += 1;
    return accountMeta;
  };
  return {
    programAddress: instruction.programAddress,
    accounts: {
      taker: getNextAccount(),
      maker: getNextAccount(),
      takerAtaFrom: getNextAccount(),
      takerAtaTo: getNextAccount(),
      makerAtaTo: getNextAccount(),
      escrow: getNextAccount(),
      escrowVault: getNextAccount(),
      mintMaker: getNextAccount(),
      mintTaker: getNextAccount(),
      systemProgram: getNextAccount(),
      mintMakerTokenProgram: getNextAccount(),
      mintTakerTokenProgram: getNextAccount(),
      associatedTokenProgram: getNextAccount(),
    },
    data: getTakeInstructionDataDecoder().decode(instruction.data),
  };
}
